/// <reference types="node" />
import { inspect } from 'util';
import VK from '../vk';
import Request from './request';
import { APIError } from '../errors';
/**
 * Working with API methods
 */
export default class API {
    private queue;
    private started;
    private suspended;
    private vk;
    /**
     * Constructor
     */
    constructor(vk: VK);
    /**
     * Returns custom tag
     */
    readonly [Symbol.toStringTag]: string;
    /**
     * Returns the current used API version
     */
    readonly API_VERSION: string;
    /**
     * Call execute method
     */
    execute(params: object): Promise<any>;
    /**
     * Call execute procedure
     */
    procedure(name: string, params: object): Promise<any>;
    /**
     * Call raw method
     */
    call(method: string, params: object): Promise<any>;
    /**
     * Adds request for queue
     */
    callWithRequest(request: Request): Promise<any>;
    /**
     * Adds method to queue
     */
    enqueue(method: string, params: object): Promise<any>;
    /**
     * Adds an element to the beginning of the queue
     */
    protected requeue(request: Request): void;
    /**
     * Running queue
     */
    private worker;
    /**
     * Calls the api method
     */
    protected callMethod(request: Request): Promise<void>;
    /**
     * Error API handler
     */
    handleError(request: Request, error: APIError): Promise<void>;
    /**
     * Custom inspect object
     */
    [inspect.custom](depth: number, options: Record<string, any>): string;
}
